import React from 'react';
import { StyleSheet, Text, View, SafeAreaView, TouchableOpacity } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
// import useCachedResources from './hooks/useCachedResources';
import LogoNailsSys from './src/Icons/LogoNailsSys'
//!expo
import * as Font from 'expo-font';
import AppLoading from 'expo-app-loading';


//! comps
import HomeScreen from './screens/HomeScreen';
import AuthScreen from './screens/AuthScreen';
import CategoryScreen from './screens/CategoryScreen';

const App = () => {
    const [isLoadingComplete, setIsLoadingComplete] = React.useState(false);
    
    const fetchFonts = async () => {
        await Font.loadAsync({
            'Roboto-Black': require('./assets/fonts/Roboto-Black.ttf'),
            'Roboto-BlackItalic': require('./assets/fonts/Roboto-BlackItalic.ttf'),
            'roboto-Bold': require('./assets/fonts/Roboto-Bold.ttf'),
            'Roboto-BoldItalic': require('./assets/fonts/Roboto-BoldItalic.ttf'),
            'Roboto-Italic': require('./assets/fonts/Roboto-Italic.ttf'),
            'Roboto-Light': require('./assets/fonts/Roboto-Light.ttf'),
            'Roboto-LightItalic': require('./assets/fonts/Roboto-Light.ttf'),
            'Roboto-Medium': require('./assets/fonts/Roboto-Medium.ttf'),
            'Roboto-MediumItalic': require('./assets/fonts/Roboto-MediumItalic.ttf'),
            'roboto-Regular': require('./assets/fonts/Roboto-Regular.ttf'),
            'roboto-Thin': require('./assets/fonts/Roboto-Thin.ttf'),
            'Roboto-ThinItalic': require('./assets/fonts/Roboto-ThinItalic.ttf'),
        });
    };

    if (!isLoadingComplete) {
        return <AppLoading startAsync={() => fetchFonts()} onFinish={() => setIsLoadingComplete(true)} onError={() => console.warn} />;
    }


    const Stack = createNativeStackNavigator();

    return (
        // <NavigationContainer>
        //     <Stack.Navigator initialRouteName="Category">
        //         <Stack.Screen name="Home" component={HomeScreen} />
        //         <Stack.Screen name="Auth" component={AuthScreen} />
        //         <Stack.Screen name="Category" component={CategoryScreen} />
        //     </Stack.Navigator>
        // </NavigationContainer>

        <SafeAreaView style={{ flex: 1, justifyContent: 'center', alignItems: 'center', backgroundColor: '#fff' }}>
            <View style={{marginTop: 20}}>
                <Text style={{ fontSize: 30, fontWeight: 'bold', color: '#141466' }}>NAILSYS</Text>
            </View>
            <View style={{ }}>
            </View>
                <View style={{flex: 1, }}>
                    <LogoNailsSys width={300} height={300} transform={[{rotate: 0.3}]}/>
                </View>

            <TouchableOpacity
                activeOpacity={0.75}
                style={{
                    backgroundColor: '#6677CC',
                    width: '90%',
                    padding: 20,
                    borderRadius: 5,
                    flexDirection: 'row',
                    justifyContent: 'space-between',
                }}
            >
                <Text style={{ fontWeight: 'bold', fontSize: 18, color: '#fff', fontFamily: 'Roboto-MediumItalic' }}>Let's Begin</Text>
                <MaterialIcons name="arrow-forward-ios" size={22} color="#fff" />
            </TouchableOpacity>
        </SafeAreaView>
    );
};

export default App;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
});
